function openAnalytics() {

    if(isUserLoggedIn()){
        document.getElementById("old_posts").style.display = "flex";
        const old_posts_container = document.getElementById("old_posts_container");
        old_posts_container.innerHTML = "Loading ...";

        makeApiRequest("/me/posts", true).then(data => {
            console.log(data);

            old_posts_container.innerHTML = "";
            for (let i = 0; i < data.length; i++) {
                const post = data[i];

                const li = document.createElement("li");
                const title = document.createElement("span");
                title.textContent = post.title;

                li.appendChild(title);
                const stats = document.createElement("div");
                stats.innerHTML = '<div><span><i class="fa-solid fa-eye"></i> ' + post.viewsCount + ' &nbsp;&nbsp;<i class="fa-solid fa-heart"></i> ' + post.likesCount + ' &nbsp;&nbsp;<i class="fa-solid fa-heart-crack"></i> ' + post.dislikesCount + ' &nbsp;&nbsp;<i class="fa-solid fa-user-plus"></i> ' + post.followersCount + ' &nbsp;&nbsp;<i class="fa-solid fa-link"></i> ' + post.linkClicksCount + '</span> <a href="/post/' + post.id + '">open</a></div>';

                li.appendChild(stats);

                old_posts_container.appendChild(li);
            }
        });
    }else{
        openRegisterModal();
    }



}

function closeAnalytics() {
    document.getElementById("old_posts").style.display = "none";
}
function loadUserData(){
    if(MainPage){
        document.getElementById("sign_in").style.display = "none";
        document.getElementById("add_post").style.display = "none";
    }


    makeApiRequest("/me").then(data => {
        window.user = data;

        if(!MainPage){
            return;
        }

        updateUsername();
        updateLevel();

        

        const oldUser = {
            xp: 0,
            level: window.user.level,
            xp_required: window.user.xp_required
        };

        setXPProgress(oldUser, true);

        document.getElementById("sign_in").style.display = "none";
        if(window.innerWidth <= 768){
            document.getElementById("add_post").style.display = "block";
        }

        document.getElementById("xp_bar").style.display = "block";

        loading_steps--;
        hideLoading();

        handleReferral();

    }).catch( error => {
        document.getElementById("sign_in").style.display = "block";
        if(window.innerWidth <= 768){
            document.getElementById("add_post").style.display = "block";
        }
        document.getElementById("add_post").onclick = openRegisterModal;
        loading_steps--;
        hideLoading();
    })
}
